import React, { useState } from "react";

import API from "../../utils/api";

function SearchForm() {
  const [inputState, setInputState] = useState({
    input: "",
  });

  const [booksState, setBooksState] = useState({
    books: {},
  });

  //Handle Btn Click
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await console.log("%cAPI Call", "color:limegreen");
      const books = await API.getBooks(inputState.input);

      await console.log("%cReturns The array", "color:limegreen");
      let array = await books.data.items;
      console.log(array);
      return array;
    } catch (err) {
      console.log(err);
    }
  };
  //Update books
  const booksUpdate = (books) => {
    setBooksState({ ...booksState, books: books });
  };

  const handleInputChange = (event) => {
    const { value } = event.target;
    setInputState({ ...inputState, input: value });
  };

  return (
    <form>
      <input onChange={handleInputChange} name="search" type="text"></input>
      <button onClick={handleSubmit} type="submit">
        Search
      </button>
    </form>
  );
}
export default SearchForm;
