import React, { useState } from "react";
import Search from "../../pages/Search";
import API from "../../utils/api";

function SearchForm() {
  const [searchState, setSearchState] = useState({
    search: "",
  });
  const [inputState, setInputState] = useState({
    input: "",
  });

  const [booksState, setBooksState] = useState({
    books: {},
  });

  //Handle Btn Click
  async function handleSubmit(event) {
    event.preventDefault();

    await setSearchState({ ...searchState, search: inputState });
    console.log(Search)
    const books = await API.getBooks(searchState);
    await booksUpdate(books);
  }
  //Update books
  const booksUpdate = (books) => {
    setBooksState({ ...booksState, books: books });
  };

  const handleInputChange = (event) => {
    const { value } = event.target;
    setInputState({ ...inputState, input: value });
  };

  return (
    <form>
      <input onChange={handleInputChange} name="search" type="text"></input>
      <button onClick={handleSubmit} type="submit">
        Search
      </button>
    </form>
  );
}
export default SearchForm;
