import React, { useState } from "react";

import API from "../../utils/api";

function SearchForm() {
  const [searchState, setSearchState] = useState({
    search: "",
  });
  const [inputState, setInputState] = useState({
    input: "",
  });

  const [booksState, setBooksState] = useState({
    books: {},
  });

  //Handle Btn Click
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await setSearchState({ ...searchState, search: inputState.input })
        .then(() => {
          const books = API.getBooks(searchState.search);
          return books;
        })
        .then((books) => {
          booksUpdate(books.data.items);
          books.data.items.forEach((book) => {
            console.log(book.volumeInfo.title);
          });

        }).then(()=>{

        });
    } catch (err) {
      console.log(err);
    }
  };
  //Update books
  const booksUpdate = (books) => {
    setBooksState({ ...booksState, books: books });
  };

  const handleInputChange = (event) => {
    const { value } = event.target;
    setInputState({ ...inputState, input: value });
  };

  return (
    <form>
      <input onChange={handleInputChange} name="search" type="text"></input>
      <button onClick={handleSubmit} type="submit">
        Search
      </button>
    </form>
  );
}
export default SearchForm;
